<?php	// Контроллер	class IndexController extends Controller	{		public $defaultAction = "Index";				// Папка вьюх		protected $_viewsFolder	= "index";						public function actionIndex()		{			// Если входим			if (count($_POST)) {				// Находим пользователя				$User = User::find(array(					"condition" => "login='{$_POST['login']}' AND password='{$_POST['password']}'",				));									if ($User) {										// Входим в сессию					$User->toSession();										$this->redirect(array(						"controller" 	=> "profile",					));				} else {					echo "<h1>Не удалось войти</h1>";				}			}						$this->render("index");		}			}