<?php	// Контроллер пользователей	class UserController extends Controller	{		public $defaultAction = "Test";				// Тестовая функция		public function actionTest()		{			addJs("fancyInput");	// Включаем JS			addJs("user");			// Включаем JS			addCss("fancyInput");	// Включаем CSS						$User = User::findById(1);									$this->render("test", array(				"User" => $User,			));		}				// Функция для добавления мысли о человеке		public function actionAjaxAddThought()		{			$User = User::findById(1);						$Adjective = $User->addAdjective($_POST["adjective"]);															echo $Adjective->adjective." добавлено!";		}	}